import React from "react";
import styled from "styled-components";

import { Box, H1, H2, Paragraph } from "../../../components/common";
import { List, Icon } from "semantic-ui-react";

import { ContactBtn, Cover } from "../";

const Wrapper = styled(Box)`
  position: relative;
  object-fit: cover !important;
  padding: 20px 0 10px 0;
`;

const Description = styled(Box)`
  text-align: center;
  margin: 0 auto;
  padding-bottom: 20px;
  max-width: 650px;
  min-width: 340px;

  @media screen and (min-width: 1024px) {
    max-width: 650px;
  }
`;

const Service = styled(Box)`
  padding: 20px;
  border: 1px solid #777777;
  flex-direction: column;
  width: 100%;
  flex: 1;
  height: 100%;
  text-align: center;

  &:first-child {
    margin-bottom: 20px;
  }

  @media screen and (min-width: 1024px) {
    width: 50%;
    display: table-cell;

    &:first-child {
      padding-right: 30px;
      margin-bottom: 0;
    }
  }
`;

const ServiceTitle = styled(H1)`
  font-size: 25px;
  text-transform: uppercase;
  color: #ff995f;
`;

const Title = styled(H1)`
  color: #fff;
  font-size: 36px;
  font-weight: bold !important;
  text-transform: uppercase;
  margin-bottom: 30px;
  font-family: LatoBold, sans-serif !important;

  @media screen and (min-width: 1024px) {
    font-size: 36px;
  }
`;

const Text = styled(H2)`
  font-size: 20px;
  color: #fff;
  letter-spacing: 1px;
  font-family: LatoRegular, sans-serif !important;

  @media screen and (min-width: 1024px) {
    font-size: 26px;
  }
`;

// TODO: make it just for ContactBtn
const ApplicBtn = styled(Box)`
  display: flex;
  justify-content: center;
  margin-top: 10px;
`;

const BgImage = styled(Box)`
  background: url(main.jpeg) center;
  background-size: cover;
  min-height: 400px;
  opacity: 0.3;
`;

const DescriptionContainer = styled(Box)`
  position: absolute;
  top: 25%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

const FeaturesList = styled(Box)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 20px;
  width: 90%;
  margin: 20px auto 20px auto;

  @media screen and (min-width: 1024px) {
    flex-direction: row;
  }
`;

const ServiceWrapper = styled.div`
    display: flex;
    width: 100%;
    flex-direction: column;

    @media screen and (min-width: 1024px) {
        display: table;
        border-collapse: separate;
           border-spacing: 20px;
    }
`;

export const WebinarDescription = () => (
  <Wrapper>
    <BgImage />
    <DescriptionContainer>
      <Description>
        <Title>IT Решения для вашего Бизнеса.</Title>
      </Description>
    </DescriptionContainer>
    <Cover id="webinar" text="Мы делаем" />

    <FeaturesList>
      <ServiceWrapper>
        <Service>
          <ServiceTitle>Мобильные приложения</ServiceTitle>
          <Text>
            Cоздаем приложения для всех платформ: Android, iOS, AppGallery​
          </Text>
        </Service>
        <Service>
          <ServiceTitle>Интернет Магазины</ServiceTitle>
          <Text>
            Создаем фирменный дизайн, интегрируем платежные системы и отправляем
            сайт в production 🚀​
          </Text>
        </Service>
      </ServiceWrapper>
    </FeaturesList>
    <FeaturesList>
      <ServiceWrapper>
        <Service>
          <ServiceTitle>WEB - ПРИЛОЖЕНИЯ​</ServiceTitle>
          <Text>
            Делаем административные панели, интегрируем сторонние сервисы.​​
          </Text>
        </Service>
        <Service>
          <ServiceTitle>LANDING-PAGES​</ServiceTitle>
          <Text>Сайты привлекающие клиентов, сотрудников, любовников.​​​</Text>
        </Service>
      </ServiceWrapper>
    </FeaturesList>
    <ApplicBtn>
      <ContactBtn />
    </ApplicBtn>
  </Wrapper>
);
